mapper.update:
update ${auction}
set ${memberNickname} = (
	select ${memberNickname}
	from ${member}
	where ${memberId}=#{memberId}
)
where ${memberId}=#{memberId}

mapper.select:
select *
from ${auction}
where ${artNo}=#{artNo}
order by ${auctionMoney} desc

		select * from ${auction}
		inner join ${art}
		on ${auction}.${artNo} = ${art}.${artNo}
        where ${auctionDate}
        in (select max(${auctionDate})
        from ${auction}
        group by ${artNo})        
        order by ${auctionMoney} desc
